mapper.select:
SELECT

,gysmc,mx.id mxId, mx.sl,mx.je,mx.kpsl,mx.fksl,mx.tksl,mx.kpje,mx.fkje,mx.tkje
FROM orderqt_ysfd ysf
LEFT JOIN system_gys gys ON ysf.gys_id = gys.id
LEFT JOIN orderqt_ysfdmx mx on ysf.id = mx.pid


    and ysf.djbh like CONCAT('%',#{ysf.djbh},'%')


    and ysf.djrq between #{ysf.startTime} and #{ysf.ysf.endTime}


    and ysf.sh = #{ysf.sh}


    and yf.gysId = #{ysf.gysId}


    and gys.gysmc like CONCAT('%',#{ysf.gysmc},'%')

SELECT

,gysmc,mx.id mxId, mx.sl,mx.je,mx.kpsl,mx.fksl,mx.tksl,mx.kpje,mx.fkje,mx.tkje
FROM orderqt_ysfd ysf
LEFT JOIN system_gys gys ON ysf.gys_id = gys.id
LEFT JOIN orderqt_ysfdmx mx on ysf.id = mx.pid


    and djbh like CONCAT('%',#{djbh},'%')


    and djrq between #{startTime} and #{endTime}


    and sh = #{sh}


    and yf.gysId = #{gysId}


    and gys.gysmc like CONCAT('%',#{gysmc},'%')
