SELECT
,u.USERNAME userName,bm.bmmc,gys.gysmc,ck.ckmc,fpsd.fpsdmc,fplx.fplxmc,djlx.djlxmc,cu.USERNAME cgfzrmc,xu.USERNAME xdfzrmc ,mx.id mxId,mx.sl,mx.dj,mx.je,sp.spdm,sp.spmc,sp.memo,dw.jldwmc FROM purchase_cg cg LEFT JOIN t_user u ON cg.user_id = u.USER_ID LEFT JOIN system_bumeng bm on cg.bm_id = bm.id LEFT JOIN system_gys gys on cg.gys_id = gys.id LEFT JOIN system_cangku ck ON cg.cangku_id = ck.id LEFT JOIN basic_fpsd fpsd ON cg.fpsl_id = fpsd.id LEFT JOIN basic_fplx fplx ON cg.fplx_id = fplx.id LEFT JOIN basic_djlx djlx ON cg.djlx_id = djlx.id LEFT JOIN t_user cu on cg.cgfzr = cu.USER_ID LEFT JOIN t_user xu on cg.xdfzr = xu.USER_ID LEFT JOIN purchase_cgmx mx ON cg.id = mx.pid LEFT JOIN shangpin sp ON mx.sp_id = sp.id LEFT JOIN basic_jldw dw ON sp.jldw_id = dw.id
and cg.djbh like CONCAT('%',#{cg.djbh},'%')
and cg.xdrq between #{cg.startTime} and #{cg.endTime}
and cg.xsdh like CONCAT('%',#{cg.xsdh},'%')
and cg.ydjh like CONCAT('%',#{cg.ydjh},'%')
and u.USERNAME like CONCAT('%',#{cg.userName},'%')
and bmmc like CONCAT('%',#{cg.bmmc},'%')
and gysmc like CONCAT('%',#{cg.gysmc},'%')
and ckmc like CONCAT('%',#{cg.ckmc},'%')
and fpsdmc like CONCAT('%',#{cg.fpsdmc},'%')
and fplxmc like CONCAT('%',#{cg.fplxmc},'%')
and djlxmc like CONCAT('%',#{cg.djlxmc},'%')
and cu.USERNAME like CONCAT('%',#{cg.cgfzrmc},'%')
and xu.USERNAME like CONCAT('%',#{cg.xdfzrmc},'%')
and cg.sfdz = #{cg.sfdz}
and cg.sfjj = #{cg.sfjj}
and cg.wldh like CONCAT('%',#{cg.wldh},'%')
and cg.yqdhrq = #{cg.yqdhrq}
and sp.spdm like CONCAT('%',#{cg.spdm},'%')
and cg.contact like CONCAT('%',#{cg.contact},'%')
and cg.qr = #{cg.qr}
and cg.qrr like CONCAT('%',#{cg.qrr},'%')
and cg.sh = #{cg.sh}
and cg.auditor like CONCAT('%',#{cg.auditor},'%')
and cg.kssh = #{cg.kssh}
and cg.ksshr like CONCAT('%',#{cg.ksshr},'%')
and cg.shqr = #{cg.shqr}
and cg.shqrr like CONCAT('%',#{cg.shqrr},'%')
and cg.cgfh = #{cg.cgfh}
and cg.cgfhr like CONCAT('%',#{cg.cgfhr},'%')
and cg.zs = #{cg.zs}
and cg.zsr like CONCAT('%',#{cg.zsr},'%')
and cg.ksfh = #{cg.ksfh}
and cg.ksph = #{cg.ksph}
and cg.yflr = #{cg.yflr}
and cg.ecshr like CONCAT('%',#{cg.ecshr},'%')
and cg.ysflr = #{cg.ysflr}

SELECT
,u.USERNAME userName,bm.bmmc,gys.gysmc,ck.ckmc,fpsd.fpsdmc,fplx.fplxmc,djlx.djlxmc,cu.USERNAME cgfzrmc,xu.USERNAME xdfzrmc ,mx.id mxId,mx.sl,mx.dj,mx.je,sp.spdm,sp.spmc,sp.memo,dw.jldwmc FROM purchase_cg cg LEFT JOIN t_user u ON cg.user_id = u.USER_ID LEFT JOIN system_bumeng bm on cg.bm_id = bm.id LEFT JOIN system_gys gys on cg.gys_id = gys.id LEFT JOIN system_cangku ck ON cg.cangku_id = ck.id LEFT JOIN basic_fpsd fpsd ON cg.fpsl_id = fpsd.id LEFT JOIN basic_fplx fplx ON cg.fplx_id = fplx.id LEFT JOIN basic_djlx djlx ON cg.djlx_id = djlx.id LEFT JOIN t_user cu on cg.cgfzr = cu.USER_ID LEFT JOIN t_user xu on cg.xdfzr = xu.USER_ID LEFT JOIN purchase_cgmx mx ON cg.id = mx.pid LEFT JOIN shangpin sp ON mx.sp_id = sp.id LEFT JOIN basic_jldw dw ON sp.jldw_id = dw.id
and cg.djbh like CONCAT('%',#{djbh},'%')
and cg.xdrq between #{startTime} and #{endTime}
and cg.xsdh like CONCAT('%',#{xsdh},'%')
and cg.ydjh like CONCAT('%',#{ydjh},'%')
and u.USERNAME like CONCAT('%',#{userName},'%')
and bmmc like CONCAT('%',#{bmmc},'%')
and gysmc like CONCAT('%',#{gysmc},'%')
and ckmc like CONCAT('%',#{ckmc},'%')
and fpsdmc like CONCAT('%',#{fpsdmc},'%')
and fplxmc like CONCAT('%',#{fplxmc},'%')
and djlxmc like CONCAT('%',#{djlxmc},'%')
and cu.USERNAME like CONCAT('%',#{cgfzrmc},'%')
and xu.USERNAME like CONCAT('%',#{xdfzrmc},'%')
and cg.sfdz = #{sfdz}
and cg.sfjj = #{sfjj}
and cg.wldh like CONCAT('%',#{wldh},'%')
and cg.yqdhrq = #{yqdhrq}
and sp.spdm like CONCAT('%',#{spdm},'%')
and cg.contact like CONCAT('%',#{contact},'%')
and cg.qr = #{qr}
and cg.qrr like CONCAT('%',#{qrr},'%')
and cg.sh = #{sh}
and cg.auditor like CONCAT('%',#{auditor},'%')
and cg.kssh = #{kssh}
and cg.ksshr like CONCAT('%',#{ksshr},'%')
and cg.shqr = #{shqr}
and cg.shqrr like CONCAT('%',#{shqrr},'%')
and cg.cgfh = #{cgfh}
and cg.cgfhr like CONCAT('%',#{cgfhr},'%')
and cg.zs = #{zs}
and cg.zsr like CONCAT('%',#{zsr},'%')
and cg.ksfh = #{ksfh}
and cg.ksph = #{ksph}
and cg.yflr = #{yflr}
and cg.ecshr like CONCAT('%',#{ecshr},'%')
and cg.ysflr = #{ysflr}
