mapper.select:
SELECT
,u.USERNAME userName,bm.bmmc,gys.gysmc,ck.ckmc,fpsd.fpsdmc,fplx.fplxmc,djlx.djlxmc,cu.USERNAME cgfzrmc,xu.USERNAME xdfzrmc ,mx.id mxId,mx.sl,mx.dj,mx.je,sp.spdm,sp.spmc,sp.memo,dw.jldwmc FROM purchase_tc tc LEFT JOIN t_user u ON tc.user_id = u.USER_ID LEFT JOIN system_bumeng bm on tc.bm_id = bm.id LEFT JOIN system_gys gys on tc.gys_id = gys.id LEFT JOIN system_cangku ck ON tc.cangku_id = ck.id LEFT JOIN basic_fpsd fpsd ON tc.fpsl_id = fpsd.id LEFT JOIN basic_fplx fplx ON tc.fplx_id = fplx.id LEFT JOIN basic_djlx djlx ON tc.djlx_id = djlx.id LEFT JOIN t_user cu on tc.cgfzr = cu.USER_ID LEFT JOIN t_user xu on tc.xdfzr = xu.USER_ID LEFT JOIN purchase_cgmx mx ON tc.id = mx.pid LEFT JOIN shangpin sp ON mx.sp_id = sp.id LEFT JOIN basic_jldw dw ON sp.jldw_id = dw.id
and tc.djbh like CONCAT('%',#{tc.djbh},'%')
and tc.xdrq between #{startTime} and #{tc.endTime}
and tc.xtdh like CONCAT('%',#{tc.xtdh},'%')
and tc.ydjh like CONCAT('%',#{tc.ydjh},'%')
and u.USERNAME like CONCAT('%',#{tc.userName},'%')
and bmmc like CONCAT('%',#{tc.bmmc},'%')
and gysmc like CONCAT('%',#{tc.gysmc},'%')
and ckmc like CONCAT('%',#{tc.ckmc},'%')
and fpsdmc like CONCAT('%',#{tc.fpsdmc},'%')
and fplxmc like CONCAT('%',#{tc.fplxmc},'%')
and djlxmc like CONCAT('%',#{tc.djlxmc},'%')
and cu.USERNAME like CONCAT('%',#{tc.cgfzrmc},'%')
and xu.USERNAME like CONCAT('%',#{tc.xdfzrmc},'%')
and tc.sfdz = #{tc.sfdz}
and tc.sfjj = #{tc.sfjj}
and tc.wldh like CONCAT('%',#{tc.wldh},'%')
and tc.yqdhrq = #{tc.yqdhrq}
and sp.spdm like CONCAT('%',#{tc.spdm},'%')
and tc.contact like CONCAT('%',#{tc.contact},'%')
and tc.qr = #{tc.qr}
and tc.qrr like CONCAT('%',#{tc.qrr},'%')
and tc.sh = #{tc.sh}
and tc.auditor like CONCAT('%',#{tc.auditor},'%')
and tc.kssh = #{tc.kssh}
and tc.ksshr like CONCAT('%',#{tc.ksshr},'%')
and tc.shqr = #{tc.shqr}
and tc.shqrr like CONCAT('%',#{tc.shqrr},'%')
and tc.zs = #{tc.zs}
and tc.zsr like CONCAT('%',#{tc.zsr},'%')
and tc.yflr = #{tc.yflr}
and tc.ysflr = #{tc.ysflr}

SELECT
,u.USERNAME userName,bm.bmmc,gys.gysmc,ck.ckmc,fpsd.fpsdmc,fplx.fplxmc,djlx.djlxmc,cu.USERNAME cgfzrmc,xu.USERNAME xdfzrmc ,mx.id mxId,mx.sl,mx.dj,mx.je,sp.spdm,sp.spmc,sp.memo,dw.jldwmc FROM purchase_tc tc LEFT JOIN t_user u ON tc.user_id = u.USER_ID LEFT JOIN system_bumeng bm on tc.bm_id = bm.id LEFT JOIN system_gys gys on tc.gys_id = gys.id LEFT JOIN system_cangku ck ON tc.cangku_id = ck.id LEFT JOIN basic_fpsd fpsd ON tc.fpsl_id = fpsd.id LEFT JOIN basic_fplx fplx ON tc.fplx_id = fplx.id LEFT JOIN basic_djlx djlx ON tc.djlx_id = djlx.id LEFT JOIN t_user cu on tc.cgfzr = cu.USER_ID LEFT JOIN t_user xu on tc.xdfzr = xu.USER_ID LEFT JOIN purchase_cgmx mx ON tc.id = mx.pid LEFT JOIN shangpin sp ON mx.sp_id = sp.id LEFT JOIN basic_jldw dw ON sp.jldw_id = dw.id
and tc.Id = #{id}
and tc.djbh like CONCAT('%',#{djbh},'%')
and tc.xdrq between #{startTime} and #{endTime}
and tc.xtdh like CONCAT('%',#{xtdh},'%')
and tc.ydjh like CONCAT('%',#{ydjh},'%')
and u.USERNAME like CONCAT('%',#{userName},'%')
and bmmc like CONCAT('%',#{bmmc},'%')
and gysmc like CONCAT('%',#{gysmc},'%')
and ckmc like CONCAT('%',#{ckmc},'%')
and fpsdmc like CONCAT('%',#{fpsdmc},'%')
and fplxmc like CONCAT('%',#{fplxmc},'%')
and djlxmc like CONCAT('%',#{djlxmc},'%')
and cu.USERNAME like CONCAT('%',#{cgfzrmc},'%')
and xu.USERNAME like CONCAT('%',#{xdfzrmc},'%')
and tc.sfdz = #{sfdz}
and tc.sfjj = #{sfjj}
and tc.wldh like CONCAT('%',#{wldh},'%')
and tc.yqdhrq = #{yqdhrq}
and sp.spdm like CONCAT('%',#{spdm},'%')
and tc.contact like CONCAT('%',#{contact},'%')
and tc.qr = #{qr}
and tc.qrr like CONCAT('%',#{qrr},'%')
and tc.sh = #{sh}
and tc.auditor like CONCAT('%',#{auditor},'%')
and tc.kssh = #{kssh}
and tc.ksshr like CONCAT('%',#{ksshr},'%')
and tc.shqr = #{shqr}
and tc.shqrr like CONCAT('%',#{shqrr},'%')
and tc.zs = #{zs}
and tc.zsr like CONCAT('%',#{zsr},'%')
and tc.yflr = #{yflr}
and tc.ysflr = #{ysflr}
