mapper.select:
SELECT
,mx.id mxId ,mx.sl ,mx.dj ,mx.je ,mx.sp_id spId ,mx.jsjg ,mx.jishuije ,mx.cbje ,dw.jldwmc ,ku.ckmc ,kh.khmc ,da.ptdamc ,lx.djlxmc ,sp.spdm ,sp.spmc ,sp.memo FROM order_ck ck LEFT JOIN system_cangku ku on ck.cangku_id = ku.id LEFT JOIN system_kehu kh on ck.kehu_id = kh.id LEFT JOIN basic_ptda da on ck.org_id = da.id LEFT JOIN basic_djlx lx on ck.djlx_id = lx.id LEFT JOIN order_ckmx mx on mx.pid = ck.Id LEFT JOIN shangpin sp on mx.sp_id = sp.id LEFT JOIN basic_jldw dw on sp.jldw_id = dw.id
and ck.Id = #{ck.id}
and ck.deleted = #{ck.deleted}
and ck.djrq between #{ck.startTime} and #{ck.endTime}
and ck.djbh like CONCAT('%',#{ck.djbh},'%')
and ck.ptdamc like CONCAT('%',#{ck.ptdamc},'%')
and ku.ckmc like CONCAT('%',#{ck.ckmc},'%')
and sp.spmc like CONCAT('%',#{ck.spmc},'%')
and ck.qr = #{ck.qr}
and ck.qrr = #{ck.qrr}
and ck.gb = #{ck.gb}
and ck.gbr = #{ck.gbr}
and ck.bhfh = #{ck.bhfh}
and ck.bhfhr = #{ck.bhfhr}
and ck.khmc like CONCAT('%',#{ck.khmc},'%')
and ck.ydjh like CONCAT('%',#{ck.ydjh},'%')

SELECT
,mx.id mxId ,mx.dj ,mx.je ,mx.sp_id spId ,mx.jsjg ,mx.jishuije ,mx.cbje ,dw.jldwmc ,ku.ckmc ,kh.khmc ,da.ptdamc ,lx.djlxmc ,sp.spdm ,sp.spmc ,sp.memo FROM order_ck ck LEFT JOIN system_cangku ku on ck.cangku_id = ku.id LEFT JOIN system_kehu kh on ck.kehu_id = kh.id LEFT JOIN basic_ptda da on ck.org_id = da.id LEFT JOIN basic_djlx lx on ck.djlx_id = lx.id LEFT JOIN order_ckmx mx on mx.pid = ck.Id LEFT JOIN shangpin sp on mx.sp_id = sp.id LEFT JOIN basic_jldw dw on sp.jldw_id = dw.id
and ck.Id = #{id}
and ck.deleted = #{deleted}
and ck.djrq between #{startTime} and #{endTime}
and ck.djbh like CONCAT('%',#{djbh},'%')
and ck.ptdamc like CONCAT('%',#{ptdamc},'%')
and ku.ckmc like CONCAT('%',#{ckmc},'%')
and sp.spmc like CONCAT('%',#{spmc},'%')
and ck.qr = #{qr}
and ck.qrr = #{qrr}
and ck.gb = #{gb}
and ck.gbr = #{gbr}
and ck.bhfh = #{bhfh}
and ck.bhfhr = #{bhfhr}
and khmc like CONCAT('%',#{khmc},'%')
and ck.ydjh like CONCAT('%',#{ydjh},'%')
