LEFT JOIN basic_jldw dw on sp.jldw_id = dw.id


        and tk.Id = #{id}


        and tk.deleted = #{deleted}


        and tk.djrq between #{startTime} and #{endTime}


        and tk.djbh like CONCAT('%',#{djbh},'%')


        and tk.qr = #{qr}


