SELECT
,pt.ptdamc , u.USERNAME user_name, bm.bmmc, kehu.khmc , ck.ckmc , kly.khlymc , lly.lllymc , dlx.djlxmc ,qy.khqymc, xtmx.id mxId , xtmx.sp_id spId , sp.spdm , sp.spmc , sp.memo ,xtmx.jhsl,xtmx.tzsl,xtmx.cksl,xtmx.dj,xtmx.je,xtmx.zk,xtmx.xsje,dw.jldwmc FROM order_xt xt LEFT JOIN basic_ptda pt on xt.org_id = pt.id LEFT JOIN t_user u on xt.user_id = u.USER_ID LEFT JOIN system_bumeng bm on xt.bumeng_id = bm.id LEFT JOIN system_kehu kehu on xt.kehu_id = kehu.id LEFT JOIN system_cangku ck on xt.cangku_id = ck.id LEFT JOIN basic_khly kly on xt.khly_id = kly.id LEFT JOIN basic_llly lly on xt.llly_id = lly.id LEFT JOIN basic_djlx dlx on xt.djlx_id = dlx.id LEFT JOIN basic_khqy qy on xt.khqy_id = qy.id LEFT JOIN order_xtmx xtmx on xt.id = xtmx.pid LEFT JOIN shangpin sp on xtmx.sp_id = sp.id LEFT JOIN basic_jldw dw on sp.jldw_id = dw.id
and xt.id = #{xt.id}
and xt.deleted = #{xt.deleted}
and xt.djbh like CONCAT('%',#{xt.djbh},'%')
and xt.ydbh like CONCAT('%',#{xt.ydbh},'%')
and xt.xdrq between #{xt.startTime} and #{xt.endTime}
and pt.ptdamc like CONCAT('%',#{xt.ptdamc},'%')
and xt.tk = #{xt.tk}
and xt.kp = #{xt.kp}
and xt.sh = #{xt.sh}
and xt.auditor like CONCAT('%',#{xt.auditor},'%')
and xt.zx = #{xt.zx}
and xt.zxr like CONCAT('%',#{xt.zxr},'%')
and xt.qr = #{xt.qr}
and xt.qrr like CONCAT('%',#{xt.qrr},'%')
and xt.zf = #{xt.zf}
and xt.zs = #{xt.zs}
xt.zsr like CONCAT('%',#{xt.zsr},'%')
and u.USERNAME like CONCAT('%',#{xt.userName},'%')
and xt.kssh = #{xt.kssh}
and xt.shqr = #{xt.shqr}
and xt.yflr = #{xt.yflr}
and kehu.khmc like CONCAT('%',#{xt.khmc},'%')
and kly.khlymc like CONCAT('%',#{xt.khlymc},'%')
and dlx.djlxmc like CONCAT('%',#{xt.djlxmc},'%')
and qy.khqymc like CONCAT('%',#{xt.khqymc},'%')
and bm.bmmc like CONCAT('%',#{xt.bmmc},'%')
and ck.ckmc like CONCAT('%',#{xt.ckmc},'%')
and sp.spmc like CONCAT('%',#{xt.spmc},'%')
ORDER BY xt.xdrq desc

SELECT

,pt.ptdamc , u.USERNAME user_name, bm.bmmc, kehu.khmc , ck.ckmc ,
kly.khlymc , lly.lllymc , dlx.djlxmc ,qy.khqymc,
xtmx.id mxId , xtmx.sp_id spId , sp.spdm , sp.spmc , sp.memo
,xtmx.jhsl,xtmx.tzsl,xtmx.cksl,xtmx.dj,xtmx.je,xtmx.zk,xtmx.xsje,dw.jldwmc
FROM order_xt xt
LEFT JOIN basic_ptda pt on xt.org_id = pt.id
LEFT JOIN t_user u on xt.user_id = u.USER_ID
LEFT JOIN system_bumeng bm on xt.bumeng_id = bm.id
LEFT JOIN system_kehu kehu on xt.kehu_id = kehu.id
LEFT JOIN system_cangku ck on xt.cangku_id = ck.id
LEFT JOIN basic_khly kly on xt.khly_id = kly.id
LEFT JOIN basic_llly lly on xt.llly_id = lly.id
LEFT JOIN basic_djlx dlx on xt.djlx_id = dlx.id
LEFT JOIN basic_khqy qy on xt.khqy_id = qy.id
LEFT JOIN order_xtmx xtmx on xt.id = xtmx.pid
LEFT JOIN shangpin sp on xtmx.sp_id = sp.id
LEFT JOIN basic_jldw dw on sp.jldw_id = dw.id


        and xt.id = #{id}


        and xt.deleted = #{deleted}


        and xt.djbh like CONCAT('%',#{djbh},'%')


        and xt.ydbh like CONCAT('%',#{ydbh},'%')


        and xt.xdrq between #{startTime} and #{endTime}


        and pt.ptdamc like CONCAT('%',#{ptdamc},'%')


        and xt.tk = #{tk}


        and xt.kp = #{kp}


        and xt.sh = #{sh}


        and xt.auditor like CONCAT('%',#{auditor},'%')


        and xt.zx = #{zx}


        and xt.zxr like CONCAT('%',#{zxr},'%')


        and xt.qr = #{qr}


        and xt.qrr like CONCAT('%',#{qrr},'%')


        and xt.zf = #{zf}


        and xt.zs = #{zs}


        xt.zsr like CONCAT('%',#{zsr},'%')


        and u.USERNAME like CONCAT('%',#{userName},'%')


        and xt.kssh = #{kssh}


        and xt.shqr = #{shqr}


        and xt.yflr = #{yflr}


        and kehu.khmc like CONCAT('%',#{khmc},'%')


        and kly.khlymc like CONCAT('%',#{khlymc},'%')


        and dlx.djlxmc like CONCAT('%',#{djlxmc},'%')


        and qy.khqymc like CONCAT('%',#{khqymc},'%')


        and bm.bmmc like CONCAT('%',#{bmmc},'%')


        and ck.ckmc like CONCAT('%',#{ckmc},'%')


        and sp.spmc like CONCAT('%',#{spmc},'%')



        and xt.djbh in (

            #{djbh}

        )


ORDER BY xt.xdrq desc
